import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import CardHeader from "@mui/material/CardHeader";
import Grid from "@mui/material/Grid";

export default function characterCard(prop) {
    const [dog, setDog] = useState('https://i.imgur.com/SaYqUTP.png');
    function changeDog() {
    const requestOptions = {
        method: "GET",
        redirect: "follow"
        };

        fetch("https://dog.ceo/api/breeds/image/random", requestOptions)
        .then((response) => response.json())
        .then((result) => {

            setDog(result.message)

            // let show = document.getElementById("here");
            // show.innerHTML = myFact;
        })
        .catch((error) => console.error(error))
    }
    return (
    <Grid item xs={12} md={4}>
        <Card>
        <CardMedia
            component="img"
            height="350px"
            image = {dog}
        />
        <CardHeader
            title={prop.titleVar}
            titleTypographyProps={{ align: "center" }}
            sx={{ mt: 1 }}
        />
        <CardContent sx={{ pt: 0 }}>
        <ul>
            {
                prop.descriptionVar.map((descriptionBulletPoint) => (
                    <Typography component="li">
                        {descriptionBulletPoint}
                    </Typography>
                ))
            }
        </ul>
        </CardContent>
        <CardActions>
            <Button
            variant="contained"
            className="characterButton"
            sx={{ px: 6, mx: "auto" , border: "5px solid red"}}
            onClick={() => changeDog()}
            >
            Click Me Pls!
            </Button>
        </CardActions>
        </Card>
    </Grid>
  );
}

function getQuote() {
    const requestOptions = {
        method: "GET",
        redirect: "follow"
      };

      fetch("https://uselessfacts.jsph.pl/api/v2/facts/random", requestOptions)
        .then((response) => response.json())
        .then((result) => {
            console.log(result.text)
            alert(result.text)
        })
        .catch((error) => console.error(error))
}
