import AppBar from "@mui/material/AppBar";
import Button from "@mui/material/Button";
import ButtonGroup from '@mui/material/ButtonGroup';
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardHeader from "@mui/material/CardHeader";
import CardMedia from "@mui/material/CardMedia";
import CssBaseline from "@mui/material/CssBaseline";
import Grid from "@mui/material/Grid";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import "./App.css";
import characters from './protagonists.json'
import CharacterCard from './characterCard.js'
import { useState, useEffect} from 'react';
import Box from '@mui/material/Box';
import Alert from '@mui/material/Alert';
import IconButton from '@mui/material/IconButton';
import { Collapse } from "@mui/material";
import { click } from "@testing-library/user-event/dist/click.js";




function App() {
  const [dogToShow, setDogToShow] = useState('https://i.postimg.cc/J4Qzwh0T/Correct-answer-2-1.png');
  const [allBreeds, setAllBreeds] = useState([]);
  const [breedCorrect, setBreedCorrect] = useState('');
  const [fourOptions, setFourOptions] = useState(['A', 'B', 'C', 'D']);
  // const [optionB, setOptionB] = useState('B');
  // const [optionC, setOptionC] = useState('C');
  // const [optionD, setOptionD] = useState('D');
  const [score, setScore] = useState(0);
  const [clickedYet, setClickedYet] = useState(false);
  const [correctDogImages, setCorrectDogImages] = useState([]);

  useEffect(() => {
    const requestOptions = {
      method: "GET",
      redirect: "follow"
    };
    fetch("https://dog.ceo/api/breeds/list/all", requestOptions)
      .then(response => response.json())
      .then(result => {
        setAllBreeds(Object.keys(result.message));
      })
      .catch(error => console.error(error));
  }, []); 

  const getDog = () => {
    setClickedYet(false);
    const requestOptions = {
      method: "GET",
      redirect: "follow"
    };
    fetch("https://dog.ceo/api/breeds/image/random", requestOptions)
      .then(response => response.json())
      .then(result => {
        let fromURL = result.message.slice('https://images.dog.ceo/breeds/'.length).split('/')[0];
        const breed = fromURL.includes('-') ? fromURL.split('-')[0] : fromURL;
        console.log(breed)
        setDogToShow(result.message);
        setBreedCorrect(breed);
        if (allBreeds.includes(breed)) {
          makeFourOptionsList(breed);
        } else {
          console.error(`Breed ${breed} not found in allBreeds`);
        }
      })
      .catch(error => console.error(error));
  };


  function makeFourOptionsList(breedCorrectFunc)  {
    let num0,num1,num2,num3=0;
    num0 = allBreeds.findIndex((x) => x===breedCorrectFunc)

    do {
      num1 = Math.floor(Math.random() * (allBreeds.length));
    } while (num1 === num0)

    do {
      num2 = Math.floor(Math.random() * (allBreeds.length));
    } while (num2 === num0 || num2 === num1)

    do {
      num3 = Math.floor(Math.random() * (allBreeds.length));
    } while (num3 === num0 || num3 === num1 || num3 === num2)

    let options = [num0, num1, num2, num3];

    for (let i = options.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [options[i], options[j]] = [options[j], options[i]];
    }

    // setOptionA(allBreeds[options[0]]);
    // setOptionB(allBreeds[options[1]]);
    // setOptionC(allBreeds[options[2]]);
    // setOptionD(allBreeds[options[3]]);

    let finalOptions = [];
    for (let i=0; i<options.length; i++) {
      finalOptions.push(allBreeds[options[i]])
    }
    setFourOptions(finalOptions);

    console.log(num0)
    console.log(num1)
    console.log(num2)
    console.log(num3)
  }

  const handleOptionClick = (selectedBreed) => {

    setClickedYet(true);
    if (selectedBreed === breedCorrect) {
      setScore(score + 2);
      setCorrectDogImages([...correctDogImages, dogToShow]);
    } else {
      setScore(Math.max(score - 1, 0));
    }

    setTimeout(() => {  getDog(); }, 1500);
  };

  const handleHintClick = () => {
    const wrongOptions = fourOptions.filter(option => option !== breedCorrect);
    if (wrongOptions.length > 0) {
      const randomWrongOption = wrongOptions[Math.floor(Math.random() * wrongOptions.length)];
      setFourOptions(fourOptions.filter(option => option !== randomWrongOption));
      setScore(Math.max(score - 1, 0));
    }
  };

  return (
    <div className="App">
      <CssBaseline />
      <AppBar
        position="static"
        color="default"
        elevation={0}
        sx={{ borderBottom: "1px solid lightgray" }}
      >
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Guess My Breed!!
          </Typography>
          <Button
            href="#"
            variant="outlined" 
            sx={{ my: 1, mx: 1.5 }}
            onClick={() => {getDog(); setScore(0); setCorrectDogImages([]);}}
          >
            NEW GAME
          </Button>
          <Button
            href="#"
            variant="outlined" 
            sx={{ my: 1, mx: 1.5, backgroundColor :  (score > 0) ? (score > 5 ? '#4D9B6C' : '#CA8D5A') : '#A85F5F',
              color :(score > 0) ? (score > 5 ? '#FFEB32' : '#62442F') : '#EBEBEB'
             }}
          >
            Score: {score}
          </Button>
          <Button
            href="#"
            variant="outlined"
            sx={{ my: 1, mx: 1.5 }}
            onClick={handleHintClick}


          >
            HINT
          </Button>

        </Toolbar>
      </AppBar>
      <Container maxWidth="md" sx={{ my: 4 }}>
        <Typography
          variant="h5"
          align="center"
          color="text.secondary"
          sx={{ mx: 10 }}
          id = "here"
        >

        </Typography>

      </Container>
      {/* End hero unit */}
      <Container maxWidth="lg" >



        <Grid
          container
          spacing={5}
          justifyContent="center"
          alignItems="flex-start"   
        >

        <Card>
          <CardMedia
          component="img"
          height="450px"
          image = {dogToShow}
          style={score > 5 ? ( !clickedYet ? { height: '350px', objectFit: 'cover', objectPosition: 'center', clipPath: 'inset(45% 0% 45% 0%)' } : {}) : {}}
          />
        </Card>
        </Grid>

        <Grid
          container
          spacing={5}
          justifyContent="center"
          alignItems="flex-start"   
        >
          <ButtonGroup variant="contained" aria-label="Basic button group" spacing={5}>

          {fourOptions.map((option, index) => (
            <Button  key={index} onClick={() => {

              !clickedYet && handleOptionClick(option)
            }}
            sx = {{backgroundColor : clickedYet ? (option === breedCorrect ? "green" : "red" ) : '' }}
            >

              {option}
            </Button>
          ))}


          </ButtonGroup>
        </Grid>


        <Grid
          container
          spacing={2}
          justifyContent="center"
          alignItems="center"
          sx={{ mt: 4 }}
        >
          {correctDogImages.map((image, index) => (
            <Grid item key={index}>
              <Card>
                <CardMedia
                  component="img"
                  height="100"
                  width="100"
                  image={image}
                />
              </Card>
            </Grid>
          ))}
        </Grid>

      </Container>
    </div>





  );
}


export default App;
