import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import CardHeader from "@mui/material/CardHeader";
import Grid from "@mui/material/Grid";

export default function characterCard(prop) {
  return (
    <Grid item xs={12} md={4}>
        <Card>
        <CardMedia
            component="img"
            height="350px"
            image = {prop.imageVar}
        />
        <CardHeader
            title={prop.titleVar}
            titleTypographyProps={{ align: "center" }}
            sx={{ mt: 1 }}
        />
        <CardContent sx={{ pt: 0 }}>
        <ul>
            {
                prop.descriptionVar.map((descriptionBulletPoint) => (
                    <Typography component="li">
                        {descriptionBulletPoint}
                    </Typography>
                ))
            }
        </ul>
        </CardContent>
        <CardActions>
            <Button
            variant="contained"
            className="characterButton"
            sx={{ px: 6, mx: "auto" , border: "5px solid red"}}
            onClick={() => getQuote()}
            >
            Click Me Pls!
            </Button>
        </CardActions>
        </Card>
    </Grid>
  );
}

function getQuote() {
    const requestOptions = {
        method: "GET",
        redirect: "follow"
      };

      fetch("https://uselessfacts.jsph.pl/api/v2/facts/random", requestOptions)
        .then((response) => response.text())
        .then((result) => {
            console.log(result)
            alert(result.id)
        })
        .catch((error) => console.error(error))
}
