import AppBar from "@mui/material/AppBar";
import Button from "@mui/material/Button";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardHeader from "@mui/material/CardHeader";
import CardMedia from "@mui/material/CardMedia";
import CssBaseline from "@mui/material/CssBaseline";
import Grid from "@mui/material/Grid";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import "./App.css";
import characters from './protagonists.json'
import CharacterCard from './characterCard.js'
import {useState} from 'react';

let myFact = ""






function App() {

  const [myFact, setMyFact] = useState('Click the new fact button to get new quote!');
  function getQuote() {
    const requestOptions = {
        method: "GET",
        redirect: "follow"
      };

      fetch("https://uselessfacts.jsph.pl/api/v2/facts/random", requestOptions)
        .then((response) => response.json())
        .then((result) => {

            setMyFact( result.text)

            // let show = document.getElementById("here");
            // show.innerHTML = myFact;
        })
        .catch((error) => console.error(error))
  }

  const [idea, setIdea] = useState('Click the button and I will you an idea for startup!');
  async function ideaStartup() {
    const requestOptions = {
      method: "GET",
      redirect: "follow"
    };

    fetch("https://itsthisforthat.com/api.php?json", requestOptions)
      .then((response) => response.json())
      .then((result) => console.log(result)

    )
      .catch((error) => console.error(error));
  }

  const [counter, setCounter] = useState(0);
  function handleClick() {
    setCounter(counter +1);
    // alert("Click "+ counter +" times")

  }


  return (
    <div className="App">
      <CssBaseline />
      <AppBar
        position="static"
        color="default"
        elevation={0}
        sx={{ borderBottom: "1px solid lightgray" }}
      >
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Characters Inc
          </Typography>
          <Button
            href="#"
            variant="outlined" 
            sx={{ my: 1, mx: 1.5 }}
            onClick = {() => ideaStartup()}
          >
            New Startup Idea
          </Button>
          <Button
            href="#"
            variant="outlined" 
            sx={{ my: 1, mx: 1.5 }}
            onClick = {handleClick}
          >
            Click Time - {counter}
          </Button>
          <Button
            href="#"
            variant="outlined" 
            sx={{ my: 1, mx: 1.5 }}
            onClick={() => getQuote()}
          >
            New Quote
          </Button>
        </Toolbar>
      </AppBar>
      <Container maxWidth="md" sx={{ my: 4 }}>

        <Typography
          variant="h5"
          align="center"
          color="text.primary"
          sx={{ py: 2 }}
        >
          {idea}
        </Typography>

        <Typography
          variant="h2"
          align="center"
          color="text.primary"
          sx={{ py: 2 }}
        >
          Prevalent Protagonists
        </Typography>


        <Typography
          variant="h5"
          align="center"
          color="text.secondary"
          sx={{ mx: 10 }}
          id = "here"
        >
          {myFact}

        </Typography>


      </Container>
      {/* End hero unit */}
      <Container maxWidth="lg" >
        <Grid
          container
          spacing={5}
          justifyContent="center"
          alignItems="flex-start"   
        >
        {

          characters.map( (cardItem) => (
            <CharacterCard
              titleVar={cardItem.title}
              imageVar={cardItem.pic}
              descriptionVar = {cardItem.description}
            ></CharacterCard>
            )
          )


        }
        </Grid>

      </Container>
    </div>
  );
}


export default App;
