import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import CardHeader from "@mui/material/CardHeader";
import Grid from "@mui/material/Grid";

export default function characterCard({titleVar, imageVar, descriptionVar}) {
  return (
    <Grid item xs={12} md={4}>
        <Card>
        <CardMedia
            component="img"
            height="350px"
            image = {imageVar}
        />
        <CardHeader
            title={titleVar + " Hello world"}
            titleTypographyProps={{ align: "center" }}
            sx={{ mt: 1 }}
        />
        <CardContent sx={{ pt: 0 }}>
        <ul>
            {
                descriptionVar.map((descriptionBulletPoint) => (
                    <Typography component="li">
                        {descriptionBulletPoint}
                    </Typography>
                ))
            }
        </ul>
        </CardContent>
        <CardActions>
            <Button
            variant="contained"
            className="characterButton"
            sx={{ px: 6, mx: "auto" , border: "5px solid red"}}
            // I'm trying to use custom CSS defined in the file App.css,
            // but it isn't working. Why, and how can I fix it?

            >
            Vote
            </Button>
        </CardActions>
        </Card>
    </Grid>
  );
}
