import AppBar from "@mui/material/AppBar";
import Button from "@mui/material/Button";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardHeader from "@mui/material/CardHeader";
import CardMedia from "@mui/material/CardMedia";
import CssBaseline from "@mui/material/CssBaseline";
import Grid from "@mui/material/Grid";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import "./App.css";
import characters from './protagonists.json'

function App() {
  return (
    <div className="App">
      <CssBaseline />
      <AppBar
        position="static"
        color="default"
        elevation={0}
        sx={{ borderBottom: "1px solid lightgray" }}
      >
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Characters Inc
          </Typography>
          <Button
            href="#"
            variant="outlined"
            sx={{ my: 1, mx: 1.5 }}
            onClick={() => alert("Boop!")}
          >
            Button
          </Button>
        </Toolbar>
      </AppBar>
      <Container maxWidth="md" sx={{ my: 4 }}>
        <Typography
          variant="h2"
          align="center"
          color="text.primary"
          sx={{ py: 2 }}
        >
          Prevalent Protagonists
        </Typography>
        <Typography
          variant="h5"
          align="center"
          color="text.secondary"
          sx={{ mx: 10 }}
        >
          Hmmmmmm, seems like we're missing some of the other protagonists.
        </Typography>
      </Container>
      {/* End hero unit */}
      <Container maxWidth="lg" >
        <Grid
          container
          spacing={5}
          justifyContent="center"
          alignItems="flex-start"   
        >
        {

          characters.map( (cardItem) => (
            <Grid item xs={12} md={4}>
              <Card>
                <CardMedia
                  component="img"
                  height="350px"
                  image={cardItem.pic}
                />
                <CardHeader
                  title={cardItem.title}
                  titleTypographyProps={{ align: "center" }}
                  sx={{ mt: 1 }}
                />
                <CardContent sx={{ pt: 0 }}>
                <ul>
                  {cardItem.description.map((descriptionBulletPoint) => (
                    <Typography component="li">
                      {descriptionBulletPoint}
                    </Typography>
                  ))}
                </ul>
                </CardContent>
                <CardActions>
                  <Button
                    variant="contained"
                    className="characterButton"
                    sx={{ px: 6, mx: "auto" , border: "5px solid red"}}
                    // I'm trying to use custom CSS defined in the file App.css,
                    // but it isn't working. Why, and how can I fix it?

                  >
                    Vote
                  </Button>
                </CardActions>
              </Card>
            </Grid>
            )
          )

        }
        </Grid>
      </Container>
    </div>
  );
}

export default App;
